<?php
// sleep for a while so we can see the indicator in demo
if ($_POST['slow']) {
    usleep(500000);
}

if (is_array($_POST['value'])) {
    echo implode(', ', $_POST['value']) . "1";
} else {
    echo $_POST['value'] . "2";
    $edit = users::find(1);
    $edit->name = $_POST['value'];
    $edit->save();
}
